	<h1>Update Password (<?= $this->hash ?>)</h1>	<? $this->show_error(); ?>	<? $this->show_flash(); ?>	<form method="post">		<div class="field_row_wrapper">			<h2>New Password:</h2>			<?= $this->User->Validate->print_field('new_password', 'New Password', 'password', 'autocomplete="off"'); ?>		</div>		<div class="field_row_wrapper">			<h2>Confirm Password:</h2>			<?= $this->User->Validate->print_field('new_password_confirm', 'New Password', 'password', 'autocomplete="off"'); ?>		</div>		<input type="hidden" name="hash" value="<?= $this->info['raw_route'][2] ?>" />		<input type="submit" name="update_submit" value="Update Account Password" class="submit_button" />		<div class="clear"></div>		<div class="cancel_reset">Don't need to update you password? <a href="<?= $this->config['web_path']?>/">Cancel update</a>.</div>				<div class="clear"></div>	</form>
